import React from 'react';
import { Image, View, StyleSheet, Text, Dimensions } from 'react-native';
import OnboardingSwiper from 'react-native-onboarding-swiper';
import { TouchableOpacity } from 'react-native'; // Add this line
import { NavigationProp } from '@react-navigation/native';

const windowWidth = Dimensions.get('window').width;

interface MyOnboardingProps {
  navigation: NavigationProp<any>;
}
const MyOnboarding: React.FC<MyOnboardingProps> = ({ navigation }) => {
  const handleDone = () => {
    navigation.navigate('LandingPage'); // Navigate to the main app screen
  };

  return (
    <OnboardingSwiper
      containerStyles={{ flex: 1 }}
      bottomBarColor="#d90504" // Customize bottom bar color
      onDone={handleDone}
      pages={[
        {
          backgroundColor: 'transparent',
          title: '',
          subtitle: '',
          image: (
            <View style={styles.splitContainer}>
              <View style={[styles.leftPart, styles.skewedBackground]}>
                <Text style={styles.leftText}>
                  Discover delicious food from restaurants near you!
                </Text>
              </View>
              <View style={styles.rightPart}>
                <Image source={require('../LandingPage/food_delivery.png')} style={styles.image} />
              </View>
            </View>
          ),
        },
        {
          backgroundColor: '#fdeb93',
          image: <Image source={require('../LandingPage/food_delivery.png')} style={styles.image} />,
          title: 'Explore',
          subtitle: 'Discover new flavors and cuisines.',
        },
        {
          backgroundColor: '#e9bcbe',
          image: <Image source={require('../LandingPage/food_delivery.png')} style={styles.image} />,
          title: 'All Done',
          subtitle: 'You are ready to enjoy delicious meals!',
        },
      ]}
      DoneButtonComponent={props => (
        <TouchableOpacity style={styles.doneButton} onPress={props.onPress}>
          <Text style={styles.doneButtonText}>Get Started</Text>
        </TouchableOpacity>
      )}
    />
  );
};

const styles = StyleSheet.create({
  splitContainer: {
    flexDirection: 'row',
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  leftPart: {
    flex: 1,
    backgroundColor: '#8d8887',
    justifyContent: 'center',
    padding: 20,
    transform: [{ skewY: '-10deg' }],
  },
  skewedBackground: {
    marginBottom: -40,
  },
  leftText: {
    color: 'white',
    fontSize: 16,
    textAlign: 'center',
  },
  rightPart: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  image: {
    width: windowWidth * 0.6,
    height: windowWidth * 0.6,
    resizeMode: 'contain',
  },
  doneButton: {
    backgroundColor: '#fdeb93',
    padding: 10,
    borderRadius: 8,
    marginTop: 20,
  },
  doneButtonText: {
    color: 'white',
    fontSize: 18,
    fontWeight: 'bold',
    textAlign: 'center',
  },
});

export default MyOnboarding;
