import React from 'react';
import { Image, View, StyleSheet } from 'react-native';
import OnboardingSwiper from 'react-native-onboarding-swiper';
import { NavigationProp } from '@react-navigation/native'; // Import the NavigationProp type

interface MyOnboardingProps {
  navigation: NavigationProp<any>; // Use the appropriate type for your navigation stack
}
const MyOnboarding: React.FC<MyOnboardingProps> = ({ navigation }) => {
  return (
    <OnboardingSwiper
      containerStyles={{ flex: 1 }} // Ensure the swiper takes up the full screen
      pages={[
        {
          backgroundColor: '#d90504',
          title: 'Find food Restarants Near You',
          subtitle: 'Contrary to popular belief borem text. it has roots clintock.',
          imageContainerStyles: {
            flex: 1,
            justifyContent: 'center',
            alignItems: 'center',
          },
          subTitleStyles: {
            color: 'white',
          },
          titleStyles: {
            color: 'white',
            fontSize: 24,
            fontWeight: 'bold',
          },
        },
        {
          backgroundColor: '#fdeb93',
          title: 'Explore',
          subtitle: 'This is the second slide of the Onboarding Swiper.',
          image: <View style={styles.separator} />, // Add the skewed separator
        },
        {
          backgroundColor: '#fdeb93',
          image: <Image source={require('../LandingPage/1.png')} />,
          title: 'Explore',
          subtitle: 'This is the second slide of the Onboarding Swiper.',
        },
        {
          backgroundColor: '#e9bcbe',
          image: <Image source={require('../LandingPage/1.png')} />,
          title: 'All Done',
          subtitle: 'This is the third slide of the Onboarding Swiper.',
        },
      ]}
      onDone={() => {
        // Handle onboarding completion, e.g., navigate to main app screen
        navigation.navigate('LandingPage');
      }}
    />
  );
};
const styles = StyleSheet.create({
  separator: {
    width: '100%',
    height: '100%',
    backgroundColor: '#d90504', // Match the background color of the first slide
    transform: [{ skewX: '-30deg' }], // Apply skew transformation
  },
});

export default MyOnboarding;
