import React from 'react';
import { Image, View, StyleSheet, Text } from 'react-native';
import OnboardingSwiper from 'react-native-onboarding-swiper';
import { NavigationProp } from '@react-navigation/native';

interface MyOnboardingProps {
  navigation: NavigationProp<any>;
}

interface SkewedBackgroundProps {
  children: React.ReactNode;
}

const SkewedBackground: React.FC<SkewedBackgroundProps> = ({ children }) => {
  return (
    <View style={styles.skewedBg}>
      <View style={styles.content}>{children}</View>
    </View>
  );
};

const MyOnboarding: React.FC<MyOnboardingProps> = ({ navigation }) => {
  return (
    <OnboardingSwiper
      containerStyles={{ flex: 1 }}
      pages={[
        {
          backgroundColor: '#d90504',
          title: 'Find food Restarants Near You',
          subtitle: 'Contrary to popular belief borem text. it has roots clintock.',
          image: (
            <SkewedBackground>
              <Text style={styles.title}>Skewed background with React Native</Text>
              <Text style={styles.text}>
                This is a quick example to show how to get skewed backgrounds with React Native.
              </Text>
            </SkewedBackground>
          ),
          subTitleStyles: {
            color: 'white',
          },
          titleStyles: {
            color: 'white',
            fontSize: 24,
            fontWeight: 'bold',
          },
        },
        {
          backgroundColor: '#fdeb93',
          title: 'Explore',
          subtitle: 'This is the second slide of the Onboarding Swiper.',
          image: <View style={styles.separator} />,
        },
        {
          backgroundColor: '#fdeb93',
          image: <Image source={require('../LandingPage/1.png')} />,
          title: 'Explore',
          subtitle: 'This is the second slide of the Onboarding Swiper.',
        },
        {
          backgroundColor: '#e9bcbe',
          image: <Image source={require('../LandingPage/1.png')} />,
          title: 'All Done',
          subtitle: 'This is the third slide of the Onboarding Swiper.',
        },
      ]}
      onDone={() => {
        navigation.navigate('LandingPage');
      }}
    />
  );
};

const styles = StyleSheet.create({
  separator: {
    width: '100%',
    height: '100%',
    backgroundColor: '#d90504',
    transform: [{ skewX: '-10deg' }], // Adjust the skew angle
  },
  skewedBg: {
    backgroundColor: '#00B285',
    padding: 200,
    transform: [{ skewY: '-10deg' }],
    marginTop: -200,
  },
  content: {
    transform: [{ skewY: '10deg' }],
    alignItems: 'center',
  },
  title: {
    paddingTop: 100,
    fontWeight: 'normal',
    color: '#FFF',
  },
  text: {
    width: '60%',
    margin: 25,
    color: '#CCC',
  },
});

export default MyOnboarding;
