import React from 'react';
import { Dimensions, Text, View, ViewStyle } from 'react-native';

interface MyOnboardingProps {
  isLight: boolean;
  image: JSX.Element;
  title: string | JSX.Element;
  subtitle: string | JSX.Element;
  containerStyles?: ViewStyle;
  imageContainerStyles?: ViewStyle;
  allowFontScaling?: boolean;
  titleStyles?: ViewStyle;
  subTitleStyles?: ViewStyle;
}

const MyOnboarding: React.FC<MyOnboardingProps> = ({
  isLight,
  image,
  title,
  subtitle,
  containerStyles,
  imageContainerStyles,
  allowFontScaling,
  titleStyles,
  subTitleStyles,
}) => {
  const { width, height } = Dimensions.get('window');
  const portrait = height > width;

  const titleElement = typeof title === 'string' || title instanceof String ? (
    <View style={styles.padding}>
      <Text allowFontScaling={allowFontScaling} style={[styles.title, isLight ? styles.titleLight : {}, titleStyles]}>
        {title}
      </Text>
    </View>
  ) : (
    title
  );

  const subtitleElement = typeof subtitle === 'string' || subtitle instanceof String ? (
    <View style={styles.padding}>
      <Text allowFontScaling={allowFontScaling} style={[styles.subtitle, isLight ? styles.subtitleLight : {}, subTitleStyles]}>
        {subtitle}
      </Text>
    </View>
  ) : (
    subtitle
  );

  return (
    <View style={[styles.container, containerStyles, { width, height }]}>
      <View style={[styles.imageContainer, imageContainerStyles]}>{image}</View>
      {titleElement}
      {subtitleElement}
    </View>
  );
};

const { width, height } = Dimensions.get('window');
const portrait = height > width;

const styles = {
  container: {
    flex: 1,
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: portrait ? 'center' : 'flex-start',
    paddingTop: portrait ? 0 : 10,
  },
  imageContainer: {
    flex: 0,
    paddingBottom: portrait ? 60 : 10,
    alignItems: 'center',
    width: '100%',
  },
  padding: {
    paddingHorizontal: 10,
  },
  title: {
    textAlign: 'center',
    fontSize: 26,
    color: '#fff',
    paddingBottom: 15,
  },
  titleLight: {
    color: '#000',
  },
  subtitle: {
    textAlign: 'center',
    fontSize: 16,
    color: 'rgba(255, 255, 255, 0.7)',
  },
  subtitleLight: {
    color: 'rgba(0, 0, 0, 0.7)',
  },
};

export default MyOnboarding;
