import { Reducer } from 'redux';
import { AuthAction } from '../Actions/auth.Actions';
import {
  FORGOT_PASSWORD_REQUEST,
  FORGOT_PASSWORD_SUCCESS,
  FORGOT_PASSWORD_FAILURE,
} from '../Actions/authTypes';

interface AuthState {
  isLoggedIn: boolean;
  user: any | null;
  error: string | null;
  forgotPasswordLoading: boolean;
  forgotPasswordError: string | null;

}
type ForgotPasswordActionTypes =
  | typeof FORGOT_PASSWORD_REQUEST
  | typeof FORGOT_PASSWORD_SUCCESS
  | typeof FORGOT_PASSWORD_FAILURE;


const initialState: AuthState = {

  isLoggedIn: false,
  user: null,
  error: null,
  forgotPasswordLoading: false,
  forgotPasswordError: '',


};

//type AuthActionTypes = "LOGIN_REQUEST" | "LOGIN_SUCCESS" | "LOGIN_FAILURE" | "FORGOT_PASSWORD_REQUEST" | "FORGOT_PASSWORD_SUCCESS" | "FORGOT_PASSWORD_FAILURE";

const authReducer: Reducer<AuthState, AuthAction> = (state = initialState, action) => {
  switch (action.type) {
    case 'LOGIN_REQUEST':
      return {
        ...state,
        isLoggedIn: false,
        error: null,
      };
    case 'LOGIN_SUCCESS':
      return {
        ...state,
        isLoggedIn: true,
        user: action.payload.user,
        error: null,
      };
    case 'LOGIN_FAILURE':
      return {
        ...state,
        isLoggedIn: false,
        user: null,
        error: action.payload.error,
      };

    case FORGOT_PASSWORD_REQUEST:
      return {
        ...state,
        forgotPasswordLoading: true,
        forgotPasswordError: '',
      };
      case FORGOT_PASSWORD_SUCCESS:
        return {
          ...state,
          forgotPasswordLoading: false,
        };
        case FORGOT_PASSWORD_FAILURE:
          return {
            ...state,
            forgotPasswordLoading: false,
            forgotPasswordError: action.payload,
          };

    default:
      return state;
  }


};

export default authReducer;
