import React from 'react';
import { View, Image, Text, StyleSheet, Dimensions } from 'react-native';
import Onboarding from 'react-native-onboarding-swiper';

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;

const MyOnboarding = () => (
  <Onboarding
    titleStyles={{ color: 'blue' }}
    pages={[
      {
        backgroundColor: 'red', // Set a transparent background
        title: 'Two Part Slide',
        subtitle: 'This is a custom two-part slide',
        imageContainerStyles: styles.slideContainerVertical,
        image: (
          <View style={[styles.slideContainerVertical, { width: windowWidth }]}>
            <View style={[styles.half, { backgroundColor: '#848282'  }]}>
            <View style={styles.content}>
              <Image source={require('../LandingPage/burger.png')} style={styles.image} />
              <Text style={styles.title}>Part 1 Title</Text>
              <Text style={styles.subtitle}>This is the first part of the slide</Text>
            </View>
            </View>
          </View>
        ),
      },

      {
        backgroundColor: 'red',
        image: <Image source={require('../LandingPage/burger.png')} style={styles.image} />,
        title: 'The Title',
        subtitle: 'This is the subtitle that supplements the title.',
      },
      {
        backgroundColor: '#999',
        image: <Image source={require('../LandingPage/burger.png')} style={styles.image} />,
        title: 'Triangle',
        subtitle: "Beautiful, isn't it?",
      },
    ]}
  />
);

const styles = StyleSheet.create({
  slideContainerVertical: {
    flex: 1,
   
  },
  half: {
    paddingVertical: 200,
    transform: [{ skewY: '-10deg' }],
    marginTop: -700,
  },
  content: {
    transform: [{ skewY: '10deg' }],
    alignItems: 'center',
  },
  image: {
    width: 72,
    height: 72,
    resizeMode: 'contain',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
    color: 'white',
  },
  subtitle: {
    fontSize: 16,
    color: 'white',
  },
});

export default MyOnboarding;
