import React from 'react';
import { View, Image, Text, StyleSheet, Dimensions } from 'react-native';
import Onboarding from 'react-native-onboarding-swiper';

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;

const MyOnboarding = () => (
  <Onboarding
    titleStyles={{ color: 'blue' }}
    pages={[
      {
        backgroundColor: 'red', // Set a transparent background
        title: 'Two Part Slide',
        subtitle: 'This is a custom two-part slide',
        imageContainerStyles: styles.slideContainerVertical,
        image: (
          <View style={[styles.slideContainerVertical]}>
            <View style={[styles.half]}>
              <View style={styles.content}>
                <Text style={styles.title}>Part 1 Title</Text>
                <Text style={styles.subtitle}>This is the first part of the slide</Text>
              </View>
            </View>
            <View style={styles.centeredImgContainer}>
              <View style={styles.whiteCircle}>
                <Image source={require('../LandingPage/burger.png')} style={styles.image} />
              </View>
            </View>
          </View>
        ),
      },  

      {
        backgroundColor: 'red',
        image: <Image source={require('../LandingPage/burger.png')} style={styles.image} />,
        title: 'The Title',
        subtitle: 'This is the subtitle that supplements the title.',
      },
      {
        backgroundColor: '#999',
        image: <Image source={require('../LandingPage/burger.png')} style={styles.image} />,
        title: 'Triangle',
        subtitle: "Beautiful, isn't it?",
      },
    ]}
  />
);

const styles = StyleSheet.create({
  slideContainerVertical: {
    flex: 1,
    width: windowWidth,
    height: windowHeight,
    resizeMode: 'contain',
    transform: [{ skewY: '-10deg' }],
  },
  half: {
    width: windowWidth,
    height: windowHeight / 2,
    backgroundColor: '#848282',
    paddingVertical: windowHeight * 0.2,
    transform: [{ skewY: '10deg' }],
    marginTop: -windowHeight * 0.5,
  },
  content: {
    alignItems: 'center',
  },
  centeredImgContainer: {
    position: 'absolute',
    top: windowHeight / 2,  // Center vertically
    left: windowWidth / 2 - (windowHeight * 0.15) / 2, // Center horizontally
  },
  whiteCircle: {
    width: windowHeight * 0.15,
    height: windowHeight * 0.15,
    borderRadius: windowHeight * 0.075,
    backgroundColor: '#FFF',
    justifyContent: 'center',
    alignItems: 'center',
  },
  image: {
    width: 72,
    height: 72,
    resizeMode: 'contain',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
    color: 'white',
  },
  subtitle: {
    fontSize: 16,
    color: 'white',
  },
});


export default MyOnboarding;
