import React from 'react';
import { View, StyleSheet, Text } from 'react-native';
import OnboardingSwiper from 'react-native-onboarding-swiper';
import { NavigationProp } from '@react-navigation/native';

interface MyOnboardingProps {
  navigation: NavigationProp<any>;
}
interface SlideProps {
  title: string;
  subtitle: string;
  topBackgroundColor: string;
  bottomBackgroundColor: string;
}

const SlideContent: React.FC<SlideProps> = ({
  title,
  subtitle,
  topBackgroundColor,
  bottomBackgroundColor,
}) => {
  return (
    <View style={styles.contentContainer}>
      <View
        style={[
          styles.topContainer,
          { backgroundColor: topBackgroundColor, flex: 2 },
        ]}
      />
      <View
        style={[
          styles.bottomContainer,
          { backgroundColor: bottomBackgroundColor, flex: 2 },
        ]}
      >
        <View style={styles.container}>
          <Text style={styles.titleText}>{title}</Text>
          <Text style={styles.subtitleText}>{subtitle}</Text>
        </View>
      </View>
    </View>
  );
};

const MyOnboarding: React.FC<MyOnboardingProps> = ({ navigation }) => {
  return (
    <OnboardingSwiper
      containerStyles={{ flex: 1 }}
      showSkip={false}
      showNext={false}
      pages={[
        {
          backgroundColor: '#848282', // Set background color here
          title: "Find food Restaurants Near You",
          subtitle: "Contrary to popular belief, boring text has roots in a piece of classical Latin literature.",
          CustomComponent: () => (
            <SlideContent
              title="Find food Restaurants Near You"
              subtitle="Contrary to popular belief, boring text has roots in a piece of classical Latin literature."
              topBackgroundColor="#848282"
              bottomBackgroundColor="#D90504"
            />
          ),
        },
        {
          backgroundColor: '#848282', // Set background color here
          title: "Explore",
          subtitle: "This is the second slide of the Onboarding Swiper.",
          CustomComponent: () => (
            <SlideContent
              title="Explore"
              subtitle="This is the second slide of the Onboarding Swiper."
              topBackgroundColor="#848282"
              bottomBackgroundColor="#D90504"
            />
          ),
        },
        {
          backgroundColor: '#D90504', // Set background color here
          title: "All Done",
          subtitle: "This is the third slide of the Onboarding Swiper.",
          CustomComponent: () => (
            <SlideContent
              title="All Done"
              subtitle="This is the third slide of the Onboarding Swiper."
              topBackgroundColor="#848282"
              bottomBackgroundColor="#D90504"
            />
          ),
        },
      ]}
      onDone={() => {
        navigation.navigate('LandingPage');
      }}
    />
  );
};



const styles = StyleSheet.create({
  contentContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  topContainer: {
    flex: 1,
    width: '100%',
    justifyContent: 'center',
    alignItems: 'center',
  },
  bottomContainer: {
    flex: 1,
    width: '100%',
    justifyContent: 'center',
    alignItems: 'center',
  },
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    paddingHorizontal: 30,
  },
  titleText: {
    fontSize: 24,
    color: 'white',
    marginBottom: 10,
    textAlign: 'left', // Align text to start on the left
  },
  subtitleText: {
    fontSize: 16,
    color: 'white',
    textAlign: 'left', // Align text to start on the left
  },
  boldText: {
    fontWeight: 'bold',
  },
});

export default MyOnboarding;
