import { Reducer } from "redux";
import { AuthAction } from "../Actions/auth.Actions";

interface AuthState {
  isLoggedIn: boolean;
  user: any | null;
  error: string | null;
  forgotPasswordLoading: boolean;
  forgotPasswordError: string | null;
  resetPasswordLoading: boolean;
  resetPasswordError: string | null;
  isSignUp: boolean;
  isResendOpt: boolean;

}

const initialState: AuthState = {


  isLoggedIn: false,
  user: null,
  error: null,
  forgotPasswordLoading: false,
  forgotPasswordError: "",
  resetPasswordLoading: false,
  resetPasswordError: '',
  isSignUp: false,
  isResendOpt: false,


};



//type AuthActionTypes = "LOGIN_REQUEST" | "LOGIN_SUCCESS" | "LOGIN_FAILURE" | "FORGOT_PASSWORD_REQUEST" | "FORGOT_PASSWORD_SUCCESS" | "FORGOT_PASSWORD_FAILURE";

const authReducer: Reducer<AuthState, AuthAction> = (
  state = initialState,
  action
) => {
  switch (action.type) {
    case "LOGIN_REQUEST":
      return {
        ...state,
        isLoggedIn: false,
        error: null,
      };
    case "LOGIN_SUCCESS":
      return {
        ...state,
        isLoggedIn: true,
        user: action.payload.user,
        error: null,
      };
    case "LOGIN_FAILURE":
      return {
        ...state,
        isLoggedIn: false,
        user: null,
        error: action.payload.error,
      };

    case 'FORGOT_PASSWORD_REQUEST':
      return {
        ...state,
        forgotPasswordLoading: true,
        forgotPasswordError: '',
      };
    case 'FORGOT_PASSWORD_SUCCESS':
      return {
        ...state,
        forgotPasswordLoading: false,
      };
    case 'FORGOT_PASSWORD_FAILURE':
      return {
        ...state,
        forgotPasswordLoading: false,
        forgotPasswordError: action.payload.error,
      };

    case 'RESET_PASSWORD_REQUEST':
      return {
        ...state,
        resetPasswordLoading: false,
        resetPasswordError: '',
      };
    case 'RESET_PASSWORD_SUCCESS':

      return {
        ...state,
        resetPasswordLoading: false,
      };
    case 'RESET_PASSWORD_FAILURE':
      return {
        ...state,
        resetPasswordLoading: false,
        resetPasswordError: action.payload.error,
      };
    case 'SIGNUP_REQUEST':
      return {
        ...state,
        isSignUp: true,
        error: null,
      };
    case 'SIGNUP_SUCCESS':
      return {
        ...state,
        isSignUp: false,
        isLoggedIn: true,
        user: action.payload.user, // Assuming the user data is available in the payload.
        error: null,
      };
    case 'SIGNUP_FAILURE':
      return {
        ...state,
        isSignUp: false,
        error: action.payload.error,
      };
    case 'RESEND_OTP_REQUEST':
      return {
        
        ...state,
        isResendOpt: true,
        signupData: action.payload, // Store signupData in the state
        error: null,
      };
    case 'RESEND_OTP_SUCCESS':
      return {
        ...state,
        isResendOpt: false,
        isLoggedIn: true,
        user: action.payload.user, // Assuming the user data is available in the payload.
        error: null,
      };
    case 'RESEND_OTP_FAILURE':
      return {
        ...state,
        isResendOpt: false,
        error: action.payload.error,
      };

    default:
      return state;
  }
};

export default authReducer;
