import React from 'react';
import { View, Image, Text, StyleSheet, Dimensions } from 'react-native';
import Onboarding from 'react-native-onboarding-swiper';

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;

const MyOnboarding = () => (
  <Onboarding
    titleStyles={{ color: '#03a60e' }}
    pages={[
      {
        backgroundColor: 'red', // Set a transparent background
        title: (
          <View style={styles.titleContainer}>
            <Text style={styles.titleText}>
              Find food{' '}
              <Text style={styles.boldText}>Restaurants Near</Text>
              {' '}
              you
            </Text>
            <Text style={styles.subtitleText}>
              Contrary to popular belief borem text. It has roots clintock
            </Text>
          </View>
        ),
        imageContainerStyles: styles.slideContainerVertical,
        image: (
          <View style={styles.slideContainerVertical}>
            <View style={styles.half}>
              <View style={styles.content}>
                <Image source={require('../LandingPage/bgImage.png')} />
                <View style={styles.imgContainer}>
                  <View style={styles.whiteCircle}>
                    <Image source={require('../LandingPage/burger.png')} style={styles.image} />
                  </View>
                </View>
              </View>
            </View>
          </View>
        ),
      },
      {
        backgroundColor: 'red',
        image: <Image source={require('../LandingPage/burger.png')} style={styles.image} />,
        title: 'The Title',
        subtitle: 'This is the subtitle that supplements the title.',
      },
      {
        backgroundColor: '#999',
        image: <Image source={require('../LandingPage/burger.png')} style={styles.image} />,
        title: 'Triangle',
        subtitle: "Beautiful, isn't it?",
      },
    ]}
  />
);

const styles = StyleSheet.create({
  slideContainerVertical: {
    flex: 1,
    width: windowWidth,
    height: windowHeight,
    resizeMode: 'contain',
  },
  half: {
    width: windowWidth,
    height: windowHeight / 6,
    paddingVertical: windowHeight * 0.2,
    marginTop: -windowHeight * 0.8,
  },
  content: {
    alignItems: 'center',
  },
  imgContainer: {
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: -windowHeight * 0.15,
    zIndex: 1,
  },
  whiteCircle: {
    width: windowHeight * 0.2,
    height: windowHeight * 0.2,
    borderRadius: windowHeight * 0.085,
    backgroundColor: '#FFF',
    justifyContent: 'center',
    alignItems: 'center',
  },
  image: {
    width: 72,
    height: 72,
    resizeMode: 'contain',
  },
  boldText: {
    fontWeight: 'bold',
  },
  titleContainer: {
    alignItems: 'center',
    marginTop: 20, // Adjust margin as needed
  },
  titleText: {
    fontSize: 30,
    marginBottom: 10,
    color: 'white',
    textAlign: 'left',
  },
  subtitleText: {
    fontSize: 16,
    color: 'white',
    textAlign: 'left',
  },
});

export default MyOnboarding;
