import React from 'react';
import { View, StyleSheet, Text, Image, TouchableOpacity } from 'react-native';
import OnboardingSwiper from 'react-native-onboarding-swiper';

const MyOnboarding = ({ navigation }) => {
  const handleDone = () => {
    navigation.navigate('LandingPage'); // Navigate to the main app screen
  };

  return (
    <OnboardingSwiper
      containerStyles={{ flex: 1 }}
      bottomBarColor="#d90504" // Customize bottom bar color
      onDone={handleDone}
      pages={[
        {
          backgroundColor: '#d90504',
          image: (
            <View style={styles.splitBox}>
              <View style={styles.leftBox}>
                <Image source={require('../LandingPage/food_delivery.png')} style={styles.image} />
              </View>
              <View style={styles.rightBox}>
                <Text style={styles.rightText}>
                  Discover delicious food from restaurants near you!
                </Text>
              </View>
            </View>
          ),
        },
        {
          backgroundColor: '#fdeb93',
          image: <Image source={require('../LandingPage/food_delivery.png')} style={styles.image} />,
          title: 'Explore',
          subtitle: 'Discover new flavors and cuisines.',
        },
        {
          backgroundColor: '#e9bcbe',
          image: <Image source={require('../LandingPage/food_delivery.png')} style={styles.image} />,
          title: 'All Done',
          subtitle: 'You are ready to enjoy delicious meals!',
        },
      ]}
      DoneButtonComponent={props => (
        <TouchableOpacity style={styles.doneButton} onPress={props.onPress}>
          <Text style={styles.doneButtonText}>Get Started</Text>
        </TouchableOpacity>
      )}
    />
  );
};

const styles = StyleSheet.create({
  splitBox: {
    flexDirection: 'row',
    flex: 1,
    alignItems: 'center',
  },
  leftBox: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  rightBox: {
    flex: 1,
    padding: 20,
    justifyContent: 'center',
  },
  rightText: {
    color: 'white',
    fontSize: 16,
    textAlign: 'center',
  },
  image: {
    width: 150,
    height: 150,
    resizeMode: 'contain',
  },
  doneButton: {
    backgroundColor: '#fdeb93',
    padding: 10,
    borderRadius: 8,
    marginTop: 20,
  },
  doneButtonText: {
    color: 'white',
    fontSize: 18,
    fontWeight: 'bold',
    textAlign: 'center',
  },
});

export default MyOnboarding;
