import React from 'react';
import { View, StyleSheet, Text } from 'react-native';
import OnboardingSwiper from 'react-native-onboarding-swiper';
import { NavigationProp } from '@react-navigation/native';

interface MyOnboardingProps {
  navigation: NavigationProp<any>;
}

const SlideContent: React.FC<{
  title: string,
  subtitle: string,
  topBackgroundColor: string,
  bottomBackgroundColor: string,
}> = ({ title, subtitle, topBackgroundColor, bottomBackgroundColor }) => {
  return (
    <View style={styles.contentContainer}>
      <View style={[styles.topContainer, { backgroundColor: topBackgroundColor }]}>
        {/* Top container with background color */}
      </View>
      <View style={[styles.bottomContainer, { backgroundColor: bottomBackgroundColor }]}>
        {/* Bottom container with background color */}
        <View style={styles.container}>
          <Text style={styles.titleText}>
            {title}
          </Text>
          <Text style={styles.subtitleText}>
            {subtitle}
          </Text>
        </View>
      </View>
    </View>
  );
};

const MyOnboarding: React.FC<MyOnboardingProps> = ({ navigation }) => {
  return (
    <OnboardingSwiper
      containerStyles={{ flex: 1 }}
      showSkip={false}
      showNext={false}
      pages={[
        {
          CustomComponent: (props) => (
            <SlideContent
              title="Find food Restaurants Near You"
              subtitle="Contrary to popular belief, boring text has roots in a piece of classical Latin literature."
              topBackgroundColor="#F29F05"
              bottomBackgroundColor="#D90504"
              {...props}
            />
          ),
        },
        {
          backgroundColor: '#F29F05',
          title: 'Explore',
          subtitle: 'This is the second slide of the Onboarding Swiper.',
        },
        {
          backgroundColor: '#D90504',
          title: 'All Done',
          subtitle: 'This is the third slide of the Onboarding Swiper.',
        },
      ]}
      onDone={() => {
        navigation.navigate('LandingPage');
      }}
    />
  );
};

const styles = StyleSheet.create({
  contentContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  topContainer: {
    flex: 1,
    width: '100%',
    justifyContent: 'center',
    alignItems: 'center',
  },
  bottomContainer: {
    flex: 1,
    width: '100%',
    justifyContent: 'center',
    alignItems: 'center',
  },
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    paddingHorizontal: 30,
  },
  titleText: {
    fontSize: 24,
    color: 'white',
    marginBottom: 10,
    textAlign: 'left', // Align text to start on the left
  },
  subtitleText: {
    fontSize: 16,
    color: 'white',
    textAlign: 'left', // Align text to start on the left
  },
  boldText: {
    fontWeight: 'bold',
  },
});

export default MyOnboarding;
