import React from 'react';
import { View, Image, Text, StyleSheet } from 'react-native';

const TwoPartSlide = ({ imageSource, title1, subtitle1, title2, subtitle2 }) => {
  return (
    <View style={styles.slide}>
      <View style={styles.halfContainer}>
        <View style={[styles.half, { backgroundColor: 'green' }]}>
          <Image source={imageSource} style={styles.image} />
          <Text style={styles.title}>{title1}</Text>
          <Text style={styles.subtitle}>{subtitle1}</Text>
        </View>
        <View style={[styles.half, { backgroundColor: 'red' }]}>
          <Text style={styles.title}>{title2}</Text>
          <Text style={styles.subtitle}>{subtitle2}</Text>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  slide: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  halfContainer: {
    flexDirection: 'row',
    width: '100%',
    height: '100%',
  },
  half: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  image: {
    width: 72,
    height: 72,
    resizeMode: 'contain',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  subtitle: {
    fontSize: 16,
    color: 'white',
  },
});

export default TwoPartSlide;
