import React from 'react';
import { Image, View, StyleSheet, Text } from 'react-native';
import OnboardingSwiper from 'react-native-onboarding-swiper';
import { NavigationProp } from '@react-navigation/native';

interface MyOnboardingProps {
  navigation: NavigationProp<any>;
}

interface SkewedBackgroundProps {
  children: React.ReactNode;
}

const SkewedBackground: React.FC<SkewedBackgroundProps> = ({ children }) => {
  return (
    <View style={styles.skewedBg}>
      <View style={styles.content}>{children}</View>
    </View>
  );
};

const MyOnboarding: React.FC<MyOnboardingProps> = ({ navigation }) => {
  return (
    <OnboardingSwiper
      containerStyles={{ flex: 1 }}
      pages={[
        {
          backgroundColor: '#d90504',
          title: 'Find Food Restaurants Near You',
          subtitle: 'Contrary to popular belief, Lorem Ipsum is not simply random text.',
          image: (
            <View style={styles.splitBox}>
              <View style={styles.leftBox}>
                <Image source={require('../assets/list.png')} style={styles.image} />
              </View>
              <View style={styles.rightBox}>
                <Text style={styles.rightText}>
                  Contrary to popular belief, Lorem Ipsum is not simply random text.
                </Text>
              </View>
            </View>
          ),
          subTitleStyles: {
            color: 'white',
          },
          titleStyles: {
            color: 'white',
            fontSize: 24,
            fontWeight: 'bold',
          },
        },
        {
          backgroundColor: '#fdeb93',
          title: 'Explore',
          subtitle: 'This is the second slide of the Onboarding Swiper.',
          image: <View style={styles.separator} />,
        },
        {
          backgroundColor: '#e9bcbe',
          image: <Image source={require('../LandingPage/1.png')} />,
          title: 'All Done',
          subtitle: 'This is the third slide of the Onboarding Swiper.',
        },
      ]}
      onDone={() => {
        navigation.navigate('LandingPage');
      }}
    />
  );
};

const styles = StyleSheet.create({
  splitBox: {
    flexDirection: 'row',
    flex: 1,
    alignItems: 'center',
  },
  leftBox: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  rightBox: {
    flex: 1,
    padding: 20,
    justifyContent: 'center',
  },
  rightText: {
    color: 'white',
    fontSize: 16,
    textAlign: 'center',
  },
  image: {
    width: 150,
    height: 150,
    resizeMode: 'contain',
  },
});

export default MyOnboarding;
