import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import OnboardingSwiper from 'react-native-onboarding-swiper';
import { NavigationProp } from '@react-navigation/native';

interface MyOnboardingProps {
  navigation: NavigationProp<any>;
}

const SlideContent: React.FC<{ title: string; subtitle: string }> = ({
  title,
  subtitle,
}) => {
  return (
    <View style={styles.contentContainer}>
      <View style={styles.container}>
        <Text style={styles.titleText}>{title}</Text>
        <Text style={styles.subtitleText}>{subtitle}</Text>
      </View>
    </View>
  );
};

const MyOnboarding: React.FC<MyOnboardingProps> = ({ navigation }) => {
  return (
    <OnboardingSwiper
      containerStyles={{ flex: 1 }}
      showSkip={false}
      showNext={false}
      pages={[
        {          customPage: SlideContent,
          backgroundColor: 'transparent', // Set the background color to transparent
          image: (
            <View style={styles.slideContainer}>
              <View style={styles.topSection} />
              <View style={styles.bottomSection}>
                <SlideContent
                  title="Find food Restaurants Near You"
                  subtitle="Contrary to popular belief, boring text has roots in a piece of classical Latin literature."
                />
              </View>
            </View>
          ),
        
          title: (
            <Text style={styles.titleText}>
              Find food <Text style={styles.boldText}>Restaurants Near</Text> You
            </Text>
          ),
          subtitle:
            'Contrary to popular belief, boring text has roots in a piece of classical Latin literature.',
        },
        {
          backgroundColor: '#fdeb93',
          title: 'Explore',
          subtitle: 'This is the second slide of the Onboarding Swiper.',
        },
        {
          backgroundColor: '#e9bcbe',
          title: 'All Done',
          subtitle: 'This is the third slide of the Onboarding Swiper.',
        },
      ]}
      onDone={() => {
        navigation.navigate('LandingPage');
      }}
    />
  );
};

const styles = StyleSheet.create({
  contentContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    paddingHorizontal: 30,
  },
  slideContainer: {
    flex: 1,
  },
  topSection: {
    flex: 1,
    backgroundColor: 'red', // Color for the top section
  },
  bottomSection: {
    flex: 1,
    backgroundColor: 'blue', // Color for the bottom section
  },
  titleText: {
    fontSize: 24,
    color: 'white',
    marginBottom: 10,
    textAlign: 'left',
  },
  subtitleText: {
    fontSize: 16,
    color: 'white',
    textAlign: 'left',
  },
  boldText: {
    fontWeight: 'bold',
  },
});

export default MyOnboarding;
