import React from 'react';
import { Image, View, StyleSheet, Text } from 'react-native';
import OnboardingSwiper from 'react-native-onboarding-swiper';
import { NavigationProp } from '@react-navigation/native';

interface MyOnboardingProps {
  navigation: NavigationProp<any>;
}

const MyOnboarding: React.FC<MyOnboardingProps> = ({ navigation }) => {
  return (
    <OnboardingSwiper
      containerStyles={{ flex: 1 }}
      showSkip={false} // Hide the skip button
      showNext={false} // Hide the next button
      pages={[
        {
          backgroundColor: '#d90504',
          image: (
            <View style={styles.container}>
              <View style={styles.content}>
                <View style={styles.splitBox}>
                  <View style={styles.leftBox}>
                    <Image source={require('../LandingPage/food_delivery.png')} style={styles.image} />
                  </View>
                  <View style={styles.rightBox}>
                    <Text style={styles.rightText}>
                      Find food Restaurants Near You
                    </Text>
                    <Text style={styles.rightDescription}>
                      Contrary to popular belief, boring text has roots in a piece of classical Latin literature.
                    </Text>
                  </View>
                </View>
              </View>
            </View>
          ),
        },
        {
          backgroundColor: '#fdeb93',
          title: 'Explore',
          subtitle: 'This is the second slide of the Onboarding Swiper.',
          image: <Image source={require('../LandingPage/food_delivery.png')} />,
        },
        {
          backgroundColor: '#e9bcbe',
          image: <Image source={require('../LandingPage/food_delivery.png')} />,
          title: 'All Done',
          subtitle: 'This is the third slide of the Onboarding Swiper.',
        },
      ]}
      onDone={() => {
        navigation.navigate('LandingPage');
      }}
    />
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  content: {
    width: '80%',
    alignItems: 'center',
  },
  splitBox: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
  },
  leftBox: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  rightBox: {
    flex: 1,
    justifyContent: 'center',
    paddingHorizontal: 20,
  },
  rightText: {
    color: 'white',
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
  },
  rightDescription: {
    color: 'white',
    fontSize: 16,
    textAlign: 'center',
  },
  image: {
    width: 150,
    height: 150,
    resizeMode: 'contain',
  },
});

export default MyOnboarding;
